const URL="https://pokeapi.co/api/v2/pokemon/",searchInput=getElement(".form-pokemon__input"),searchButton=getElement(".form-pokemon__search"),container=getElement(".pokemon"),erroMessage=getElement(".error");var namePokemon,pokemon,card;function getElement(e){return document.querySelector(e)}function requestPokeInfo(e,n){fetch(e+n).then(e=>e.json()).then(e=>{pokemon=e}).catch(e=>console.log(e))}function createCard(){return card=`\n    <figure class="pokemon__imagem"><img src="${pokemon.sprites.front_default}" alt="Sprite of ${pokemon.name}"></figure>\n    <div class="pokemon__informations">\n        <h1 class="pokemon__name">Name: ${pokemon.name}</h1>\n        <h2 class="pokemon__number">NÂº ${pokemon.id}</h2>\n        <h3 class="pokemon__type">Type: ${pokemon.types.map(e=>e.type.name).toString()}</h3>\n        <h3 class="pokemon__weight">Weight: ${pokemon.weight/10}kg</h3>\n        <h3 class="pokemon__height">Height: ${pokemon.height/10}m</h3>\n    </div>`}function startApp(e){requestPokeInfo(URL,e),setTimeout(function(){pokemon.detail?(erroMessage.style.display="block",container.style.display="none"):(erroMessage.style.display="none",container.style.display="flex",container.innerHTML=createCard())},2e3)}searchButton.addEventListener("click",e=>{e.preventDefault(),startApp(namePokemon=searchInput.value.toLowerCase())});